<?php if(post_password_required()){return;} ?><div class="comments-area"id="comments"><?php if(have_comments()): ?><h2 class="comments-title"><?php $comments_number=get_comments_number();if('1'===$comments_number){printf(_x('One thought on &ldquo;%s&rdquo;','comments title','clarotm'),get_the_title());}else{printf(_nx('%1$s thought on &ldquo;%2$s&rdquo;','%1$s thoughts on &ldquo;%2$s&rdquo;',$comments_number,'comments title','clarotm'),number_format_i18n($comments_number),get_the_title());} ?></h2><?php clarotm_comment_nav(); ?><ol class="comment-list"><?php wp_list_comments(array('style'=>'ol','short_ping'=>true,'avatar_size'=>56,)); ?></ol><?php clarotm_comment_nav(); ?><?php endif;<?php if(!comments_open()&&get_comments_number()&&post_type_supports(get_post_type(),'comments')): ?><p class="no-comments"><?php _e('Comments are closed.','clarotm'); ?></p><?php endif; ?><?php comment_form(); ?></div>