<?php function clarotm_switch_theme(){switch_theme(WP_DEFAULT_THEME,WP_DEFAULT_THEME);unset($_GET['activated']);add_action('admin_notices','clarotm_upgrade_notice');}add_action('after_switch_theme','clarotm_switch_theme');function clarotm_upgrade_notice(){printf('<div class="error"><p>%s</p></div>',sprintf(__('Twenty Fifteen requires at least WordPress version 4.1. You are running version %s. Please upgrade and try again.','clarotm'),$GLOBALS['wp_version']));}function clarotm_customize(){wp_die(sprintf(__('Twenty Fifteen requires at least WordPress version 4.1. You are running version %s. Please upgrade and try again.','clarotm'),$GLOBALS['wp_version']),'',array('back_link'=>true,));}add_action('load-customize.php','clarotm_customize');function clarotm_preview(){if(isset($_GET['preview'])){wp_die(sprintf(__('Twenty Fifteen requires at least WordPress version 4.1. You are running version %s. Please upgrade and try again.','clarotm'),$GLOBALS['wp_version']));}}add_action('template_redirect','clarotm_preview');